package programs;

public class Reversetriangle {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
				int m=10; int n=9;
				for(int i=1;i<=m;i++)
				{
					for(int j=1;j<=i;j++)
					{
						System.out.print("*");
					
				}
				System.out.println();
			}
		for(int i=1;i<n;i++)
		{
			for(int j=9;j>=i;j--)
			{
				System.out.print("*");
			}
			System.out.println();
		}
		}
		}

	


